import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/FirstPage.css'; // 스타일 파일을 임포트
import WeekDaysSlider from '../utils/weekDaysSlider';
import BannerSlider from '../utils/bannerSlider';
import { AuthContext } from '../contexts/AuthContext';
import RunningList from './RunningPage';



const FirstPage = () => {
    const navigate = useNavigate();
    const { user, logout } = useContext(AuthContext); // AuthContext에서 사용자 정보와 로그아웃 함수 가져오기
    const [selectedDate, setSelectedDate] = useState(''); // State to hold selected date


    const handlePageChange = (path) => {
        navigate(path);
    }

    const handleDateClick = (formattedDate) => {
        setSelectedDate(formattedDate); // Update selected date
    }


    return (
        <div className='container'>
            <div className='navbar'>
                <div className='userMenu'>
                    <div>
                        <h1 onClick={() => handlePageChange('/')} className='clickable-text'>RUNNING CREW</h1>
                    </div>

                    <div className='searchBox'>
                        <input type="text"
                            placeholder='지역 크루로 찾기'
                            className='searchInput'/>

                        <button
                            id='search-Btn'>
                            <img src='/images/searchImage.png' alt='search icon' className='search-Img'/>
                        </button>
                        </div>

                        <div className='login'>
                        {user ? (
                            <>
                                <p onClick={() => handlePageChange('/mypage')} className='clickable-text'>{user.nickname}</p>
                                <button onClick={logout}>로그아웃</button>
                            </>
                        ) : (
                            <h1 onClick={() => handlePageChange('/login')} className='clickable-text'>로그인</h1>
                        )}
                    </div>
                </div>
            </div>

            <div className='navigation__wrapper'>
            <div className='navigation__item'>
                    <h2 onClick={() => handlePageChange('/')} className='clickable-text'>바로러닝</h2>
                </div>
                <div className='navigation__item'>
                    <h2 onClick={() => handlePageChange('/crew')} className='clickable-text'>크루</h2>
                </div>
                <div className='navigation__item'>
                    <h2 onClick={() => handlePageChange('/challenge')} className='clickable-text'>챌린지</h2>
                </div>
                <div className='navigation__item'>
                    <h2 onClick={() => handlePageChange('/create/running')} className='clickable-text'>러닝만들기</h2>
                </div>
            </div>

            <div>
                <BannerSlider />
            </div>

            <div>
                <WeekDaysSlider onDateClick={handleDateClick} />
            </div>

            <div>
                <RunningList selectedDate={selectedDate} />
            </div>

        </div>




    );
};

export default FirstPage;
