import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../css/FirstPage.css'; // 스타일 파일을 임포트



const FirstPage = () => {
    const navigate = useNavigate();

    const currentDate = new Date();

    const formattedDate = `${currentDate.getDate()}일`;
    const week = ['일', '월', '화', '수', '목', '금', '토'];
    const dayOfweek = week[currentDate.getDay()]

    const handlePageChange = (path) => {
        navigate(path);
    }

    return (
        <div className='container'>
            <div className='navbar'>
                <div className='userMenu'>
                    <div>
                        <h1>LOGO</h1>
                    </div>
                    <div className='login'>
                        <h1 onClick={() => handlePageChange('/login')} className='clickable-text'>로그인</h1>   
                    </div>
                </div>
            </div>

            <div className='navigation__wrapper'>
                <div className='navigation__item'>
                    <h2 onClick={() => handlePageChange('/')} className='clickable-text'>팀</h2>
                </div>
                <div className='navigation__item'>
                    <h2 onClick={() => handlePageChange('/challenge')} className='clickable-text'>챌린지</h2>
                </div>
            </div>

            <div className='tabWrap'>
            <li id="datewrap" class="dateWrap slick-slide">
                <p>{formattedDate}</p> 
                <span>{dayOfweek}</span>
            </li>

            </div>

        </div>




    );
};

export default FirstPage;
