        <p>{formattedDate}</p>
        <span>{dayOfWeek}</span>
      </div>
    );
  useEffect(() => {

    setDate()
    const fetchRunnings = async () => {

      try {
        const response = await axiosInstance.get('/api/runnings/get', {
          params: {
            date: '2024-06-26',
          },
        });

        setRunnings(response.data.data);
      } catch (error) {
        console.error('Error fetching boards:', error);
        setError('게시판 목록을 가져오는 중 오류가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

