  }, [date]); // currentPage가 변경될 때마다 호출


  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
        <div className='main__filter'>
            <div className='filter--wrapper'>
                <ul>
                    <li className='clickable-text'>
                        <span>지역</span>
                    </li>
                    <li className='clickable-text'>
                        <span>마감 가리기</span>
                    </li>
                    <li className='clickable-text'>
                        <span>성별</span>
                    </li>
                    <li className='clickable-text'>
                        <span>거리별</span>
                    </li>
                </ul>
            </div>
        </div>


        <div>
        <ul className="running-list">
      {runnings.map((running) => (
        <li key={running.id} className="running-item">
          <div className="running-item__time">
            <p>{running.time}</p>
          </div>
          <div className="running-item__info">
            <h3>{running.title}</h3>
            <p>{running.content}</p>
            <div className="item-details">
              <p>제한 인원: {running.limitedPeople}명</p>
              <p>시작 위치: {running.startLocation}</p>
              <p>도착 위치: {running.finishLocation}</p>
              <p>거리: {running.distance}km</p>
            </div>
          </div>
        </li>
      ))}
    </ul>
        </div>

    </div>
  );
};

export default RunningList;
