import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../utils/axiosInstance';
import '../css/Mypage.css';

const Mypage = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [runs, setRuns] = useState([]);
    const [editMode, setEditMode] = useState(false);
    const [formData, setFormData] = useState({
        nickname: '',
        email: '',
        password: '',
    });

    useEffect(() => {
        const fetchUserDetail = async () => {
            try {
                const response = await axiosInstance.post('/api/user/profile');
                setUser(response.data);
                setFormData({
                    nickname: response.data.nickname,
                    email: response.data.email,
                    password: '',
                });
                setLoading(false);
            } catch (error) {
                alert(error.response.data.errorMessage);
                setLoading(false);
            }
        };

        const fetchUserRuns = async () => {
            try {
                const response = await axiosInstance.get('/api/user/runs');
                setRuns(response.data);
            } catch (error) {
                console.error('Error fetching user runs:', error);
            }
        };

        fetchUserDetail();
        fetchUserRuns();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        try {
            await axiosInstance.put('/api/user/update', formData);
            alert('회원 정보가 성공적으로 수정되었습니다.');
            setEditMode(false);
        } catch (error) {
            alert('회원 정보 수정 중 오류가 발생했습니다.');
        }
    };

    if (loading) {
        return <p>Loading...</p>;
    }

    return (
        <div className="container">
            <h2>마이페이지</h2>
            {user && (
                <>
                    <p>닉네임: {user.nickname}</p>
                    <p>이메일: {user.email}</p>
                </>
            )}
            <button onClick={() => setEditMode(!editMode)}>
                {editMode ? '취소' : '회원 정보 수정'}
            </button>
            {editMode && (
                <form onSubmit={handleFormSubmit} className="edit-form">
                    <label>
                        닉네임:
                        <input
                            type="text"
                            name="nickname"
                            value={formData.nickname}
                            onChange={handleInputChange}
                        />
                    </label>
                    <label>
                        이메일:
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleInputChange}
                        />
                    </label>
                    <label>
                        비밀번호:
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleInputChange}
                        />
                    </label>
                    <button type="submit">저장</button>
                </form>
            )}
            <h3>내가 했던 런닝</h3>
        </div>
    );
};

export default Mypage;
