import axiosInstance from '../utils/axiosInstance';
import '../css/Reply/ReplyForm.css';

const ReplyForm = ({ crewId, onReplySubmit }) => {
  const [replyContent, setReplyContent] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axiosInstance.post(`/api/replies/create/${crewId}`, { 
        content: replyContent,
      });
      // Logic after submitting a reply (e.g., updating the list)
      onReplySubmit(response.data.data); // Pass new reply data to parent component
      setReplyContent(''); // Reset form
    } catch (error) {
      console.error(error.response.data.data);
      alert(error.response.data.data);
    }
  };

