import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/Crew/CrewPage.css'; // 스타일 파일을 임포트
import Navbar from '../components/NavBar';
import axiosInstance from '../utils/axiosInstance';




const CrewPage = () => {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [crews, setCrews] = useState([]);
    const [location, setLocation] = useState('전남');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchCrew = async () => {
            try {
                const response = await axiosInstance.get('/api/crews/get', {
                  params: {
                    location
                  },
                });
                setCrews(response.data.data || []); // 데이터를 설정하고, 없으면 빈 배열로 초기화
                console.log(response.data.data);
              } catch (error) {
                console.error('Error fetching boards:', error);
                setError('게시판 목록을 가져오는 중 오류가 발생했습니다.');
              } finally {
                setLoading(false);
              }
        }

        fetchCrew();
    }, [location]);

    const navigateToDetail = (id) => {
        navigate(`/crew/detail/${id}`);
      };

      const renderModalContent = () => {
        switch(modalType) {
          case 'startLocation':
            return (
              <div>
                <h2>시작 지역 선택</h2>
                <ul className='filter--list'>
                  <li className='filter--list--item' onClick={() => {setSelectedStartLocation(''); closeModal(); }}>
                    <p>모든시작지역</p>
                  </li>
                  <li className='filter--list--item' onClick={() => {setSelectedStartLocation('서울'); closeModal(); }}>
                    <p>서울</p>
                  </li>
                  <li className='filter--list--item' onClick={() => {setSelectedStartLocation('전남'); closeModal(); }}>
                    <p>전남</p>
                  </li>
                </ul>
              </div>
            );
          case 'distance':
            return (
              <div>
              <h2>거리 선택</h2>
              <ul className='filter--list'>
                  <p>모든거리</p>

              </ul>
            </div>
            );
          default:
            return null;
        }
      };

    return (
        <div className='container'>
            <div>
                <Navbar />
            </div>

        {crews.length === 0 ? (
          <div className='no-data-message'><p>크루가 없습니다.</p></div>
        ):(
          <ul className="running-list">
            {crews.map((crew) => (
              <li key={crew.id} className="running-item clickable" onClick={() => navigateToDetail(crew.id)}>
                <div className="running-item__info">
                  <h3>{crew.title}</h3>
                  <p>{crew.content}</p>
                  <div className="item-details">
                    <p>시작 위치: {crew.location}</p>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        )}
        </div>
    );
};

export default CrewPage;
