import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/Crew/CrewPage.css'; // 스타일 파일을 임포트
import Navbar from '../components/NavBar';
import axiosInstance from '../utils/axiosInstance';
import Modal from 'react-modal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';




const CrewPage = () => {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [crews, setCrews] = useState([]);
    const [liked, setLiked] = useState(false); // 좋아요 상태 관리


    const [location, setLocation] = useState('');
    const navigate = useNavigate();
    const [selectedStartLocation, setSelectedStartLocation] = useState('');
    const [modalType, setModalType] = useState('');
    const [modalIsOpen, setModalIsOpen] = useState(false);  // 모달 변수 설정



    useEffect(() => {
        const fetchCrew = async () => {
            try {
                const response = await axiosInstance.get('/api/crews/get', {
                  params: {
                    location
                  },
                });
                setCrews(response.data.data || []); // 데이터를 설정하고, 없으면 빈 배열로 초기화
                console.log(response.data.data);
              } catch (error) {
                console.error('Error fetching boards:', error);
                setError('게시판 목록을 가져오는 중 오류가 발생했습니다.');
              } finally {
                setLoading(false);
              }
        }

        fetchCrew();
    }, [location]);

    const navigateToDetail = (id) => {
        navigate(`/crew/detail/${id}`);
      };


        // 좋아요 버튼 클릭 시 처리
  const handleLike = async () => {
    try {
      const response = await axiosInstance.post(`/api/likes/create/${id}`);
      setLiked(response.data.liked); // 서버 응답에 따라 좋아요 상태 업데이트
    } catch (error) {
      console.error('Error liking crew:', error);
      alert('좋아요 처리 중 오류가 발생했습니다.');
    }
  };

  const openModal = (type) => {
    setModalIsOpen(true);
    setModalType(type);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setModalType('');
  };

      const renderModalContent = () => {
        switch(modalType) {
          case 'location':
            return (
              <div>
                <h2>시작 지역 선택</h2>
                <ul className='filter--list'>
                  <li className='filter--list--item' onClick={() => {setLocation(''); closeModal(); }}>
                    <p>모든지역</p>
                  </li>
                  <li className='filter--list--item' onClick={() => {setLocation('서울'); closeModal(); }}>
                    <p>서울</p>
                  </li>
                  <li className='filter--list--item' onClick={() => {setLocation('전남'); closeModal(); }}>
                    <p>전남</p>
                  </li>
                </ul>
              </div>
            );
          case 'distance':
            return (
              <div>
              <h2>거리 선택</h2>
              <ul className='filter--list'>
                  <p>모든거리</p>

              </ul>
            </div>
            );
          default:
            return null;
        }
      };



    return (
        <div className='container'>
            <div>
                <Navbar />
            </div>

            <div className='main__filter'>
        <div className='filter--wrapper'>
          <ul>
            <li className='clickable-text' onClick={() => openModal('location')}>
              <span>{location === '' ? '모든지역' :  location}</span>
            </li>
            <li className='clickable-text'>
              <span>댓글순</span>
            </li>
            <li className='clickable-text'>
              <span>하트순</span>
            </li>
          </ul>
        </div>
      </div>

        {crews.length === 0 ? (
          <div className='no-data-message'><p>크루가 없습니다.</p></div>
        ):(
          <ul className="crew-list">
            {crews.map((crew) => (
              <li key={crew.id} className="crew-item clickable" onClick={() => navigateToDetail(crew.id)}>
                <div className="crew-item__info">
                  <h3>{crew.title}</h3>
                  <p>{crew.content}</p>

                  <div className="item-details">
                    <p>크루 지역: {crew.location}</p>
                  </div>

                </div>
                <div className='likes-container'>
                    <FontAwesomeIcon 
                    icon={faHeart} 
                    className={`clickable heart-icon ${liked ? 'liked' : ''}`}
                    onClick={handleLike}
                    />

                </div>
              </li>

            ))}
          </ul>
        )}


        <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
        >
          {renderModalContent()}
          <button onClick={closeModal}>닫기</button>
        </Modal>
        </div>
    );
};

export default CrewPage;
