import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Slider from 'react-slick'; // react-slick에서 Slider 컴포넌트 임포트
import '../css/FirstPage.css'; // 스타일 파일을 임포트
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';



const FirstPage = () => {
    const navigate = useNavigate();

    const currentDate = new Date();
    const week = ['일', '월', '화', '수', '목', '금', '토'];
    const daysToShow = 14; // 보여줄 날짜 수

    const days = [];
    for (let i = 0; i < daysToShow; i++) {
      const date = new Date();
      date.setDate(currentDate.getDate() + i);

      const formattedDate = `${date.getDate()}`;
      const dayOfWeek = week[date.getDay()];

      days.push(
        <li key={i} id="datewrap" className="dateWrap isSun slick-slide slick-active">
          <p>{formattedDate}</p>
          <span>{dayOfWeek}</span>
        </li>
      );
    }

    const handlePageChange = (path) => {
        navigate(path);
    }

    return (
        <div className='container'>
            <div className='navbar'>
                <div className='userMenu'>
                    <div>
                        <h1>LOGO</h1>
                    </div>
                    <div className='login'>
                        <h1 onClick={() => handlePageChange('/login')} className='clickable-text'>로그인</h1>   
                    </div>
                </div>
            </div>

            <div className='navigation__wrapper'>
                <div className='navigation__item'>
                    <h2 onClick={() => handlePageChange('/')} className='clickable-text'>팀</h2>
                </div>
                <div className='navigation__item'>
                    <h2 onClick={() => handlePageChange('/challenge')} className='clickable-text'>챌린지</h2>
                </div>
            </div>

            <div className='tabWrap'>
                <ul className='swipe-tab slick-initialized slick-slider'>
                    {days}
                </ul>

            </div>

        </div>




    );
};

export default FirstPage;
