// src/utils/KakaoMap.js
import React from 'react';
import { Map, MapMarker, useInjectKakaoMapApi } from 'react-kakao-maps-sdk';

const KakaoMap = () => {
  const { loading, error } = useInjectKakaoMapApi({
    appkey: process.env.REACT_APP_KAKAO_MAP_API_KEY,
  });

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error loading map</div>;

  return (
    <Map
      center={{ lat: 37.5665, lng: 126.9780 }} // 초기 중심 좌표 (서울)
      style={{ width: '100%', height: '500px' }} // 지도의 크기
      level={3} // 지도의 확대 레벨
    >
      <MapMarker position={{ lat: 37.5665, lng: 126.9780 }}>
        <div style={{ color: '#000' }}>여기가 중심입니다!</div>
      </MapMarker>
    </Map>
  );
};

export default KakaoMap;
