// src/utils/auth.js
// import jwt_decode from 'jwt-decode';

export const isTokenExpired = (token) => {
  if (!token) {
    return true;
  }

  const decoded = jwt_decode(token);
  const currentTime = Date.now() / 1000;

  return decoded.exp < currentTime;
};
