import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../utils/axiosInstance';
import KakaoMap from '../utils/KakaoMap';
import DaumPostcode from "react-daum-postcode";
import { Modal, Button } from "antd";




function App() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [message, setMessage] = useState('');
  const [distance, setDistance] = useState('');
  const [startLocation, setStartLocation] = useState('');
  const [finishLocation, setFinishLocation] = useState('');
  const [date, setDate] = useState('');

  // 주소 검색 모달
  const onToggleModal = () => {
    setIsOpen((prev) => !prev);
  };

  const handleComplete = (data) => {
    // 시.도 저장
    setQ1(data.sido);
    // 구.군 저장
    setQ3(
      data.sigungu.length > 3
        ? data.sigungu.split("").splice(0, 3).join("")
        : data.sigungu
    );

    // 상세주소 앞 2단어 제외하고 저장 ('서울 강남구' 제외하고 저장)
    let splitAddress = data.address.split(" ").splice(2).join(" ");
    setDetailaddress(splitAddress);
    onToggleModal(); // 주소창은 자동으로 사라지므로 모달만 꺼주면
  };


  const navigate = useNavigate();




  const handleSubmit = async (event) => {
    event.preventDefault();



    try {
      const response = await axiosInstance.post('/api/running/create', {
        title,
        content,
        distance,
        startLocation,
        finishLocation,
        date

      });

      if(response.status === 200) {
        navigate('/');
        setMessage(response.data);
        alert(response.data);
      }
    } catch (error) {
      setMessage(error.response.data.errorMessage);
      alert(error.response.data.errorMessage);
    }
  };

  return (
    <div>
      <h2>런닝 만들기</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>제목 :</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div>
          <label>경로설명:</label>
          <input
            type="text"
            value={content}
            onChange={(e) => setContent(e.target.value)}
          />
        </div>
        <div>
          <label>거리:</label>
          <input
            type="text"
            value={distance}
            onChange={(e) => setDistance(e.target.value)}
          />
        </div>
        <div>
          <label>시작지점:</label>
          <input
            type="text"
            value={startLocation}
            onChange={(e) => setStartLocation(e.target.value)}
          />
        </div>
        <div>
          <label>도착지점:</label>
          <input
            type="text"
            value={finishLocation}
            onChange={(e) => setFinishLocation(e.target.value)}
          />
        </div>
        <div>
          <label>date:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
        <div>
          <KakaoMap />
        </div>
        <button type="submit">완료</button>
      </form>
      <p>{message}</p>
    </div>
  );
}

export default App;
