import React, { useEffect, useState } from 'react';
import axiosInstance from '../utils/axiosInstance';

const BoardList = () => {
    const [boardList, setBoardList] = useState([]);
    const [pageList, setPageList] = useState([]);

    const [curPage, setCurPage] = useState(0); //현재 페이지 세팅
    const [prevBlock, setPrevBlock] = useState(0); //이전 페이지 블록
    const [nextBlock, setNextBlock] = useState(0); //다음 페이지 블록
    const [lastPage, setLastPage] = useState(0); //마지막 페이지

  useEffect(() => {
    const fetchBoards = async () => {
      try {
        const response = await axiosInstance.get('/api/boards/main', {
          params: {
            page: currentPage,
            pageSize: pageSize,
          }
        });

        setBoardList(response.data.data);
      } catch (error) {
        console.error('Error fetching boards:', error);
        setError('게시판 목록을 가져오는 중 오류가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

    fetchBoards();
  }, [currentPage]); // currentPage가 변경될 때마다 호출

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  // 페이지 네비게이션 버튼을 렌더링하는 함수
  const renderPaginationButtons = () => {
    const pages = [];
    const range = 3; // 현재 페이지를 중심으로 좌우로 표시될 페이지 버튼의 수

    // 이전 끝 버튼
    pages.push(
      <button key="prev-end" onClick={() => handlePageChange(0)} disabled={currentPage === 0}>
        이전 끝
      </button>
    );

    // 이전 페이지 버튼들
    for (let i = Math.max(0, currentPage - range); i < currentPage; i++) {
      pages.push(
        <button key={i} onClick={() => handlePageChange(i)}>
          {i + 1}
        </button>
      );
    }

    // 현재 페이지 버튼
    pages.push(
      <button key={currentPage} onClick={() => handlePageChange(currentPage)} disabled>
        {currentPage + 1}
      </button>
    );

    // 다음 페이지 버튼들
    for (let i = currentPage + 1; i <= Math.min(currentPage + range, totalPages - 1); i++) {
      pages.push(
        <button key={i} onClick={() => handlePageChange(i)}>
          {i + 1}
        </button>
      );
    }

    // 다음 끝 버튼
    pages.push(
      <button key="next-end" onClick={() => handlePageChange(totalPages - 1)} disabled={currentPage === totalPages - 1}>
        다음 끝
      </button>
    );

    return pages;
  };

  return (
    <div>
      <h1>Board List</h1>
      <ul>
        {boards.map((board) => (
          <li key={board.id}>
            <h2>{board.title}</h2>
            <p>{board.content}</p>
          </li>
        ))}
      </ul>
      <div>
        {/* 페이지 네비게이션 UI */}
        {renderPaginationButtons()}
      </div>
    </div>
  );
};

export default BoardList;
