import React, { useEffect, useState } from 'react';
import axiosInstance from '../utils/axiosInstance';
import '../css/Running/RunningPage.css'; // 스타일 파일을 임포트
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import '../css/WeekDaysSlider.css';
import Modal from 'react-modal';



const RunningList = () => {
  const [runnings, setRunnings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().slice(0, 10)); // 초기 날짜 설정

  const [modalIsOpen, setModalIsOpen] = useState(false);  // 모달 변수 설정

  const [modalType, setModalType] = useState('');


  const currentDate = new Date();
  const week = ['일', '월', '화', '수', '목', '금', '토'];
  const daysToShow = 14; // 14일 동안 보여줍니다

  const days = [];
  for (let i = 0; i < daysToShow; i++) {
    const date = new Date();
    date.setDate(currentDate.getDate() + i);

    const formattedViewDate = `${date.getDate()}`;
    const formattedDate = `${date.getFullYear()}-${('0' + (date.getMonth() + 1)).slice(-2)}-${('0' + date.getDate()).slice(-2)}`;
    const dayOfWeek = week[date.getDay()];



    days.push(
      <div key={i} className="dateWrap isSun clickable-text" onClick={() => handleDateClick(formattedDate)}>
        <p>{formattedViewDate}</p>
        <span>{dayOfWeek}</span>
      </div>
    );
  }

  const openModal = (type) => {
    setModalIsOpen(true);
    setModalType(type);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const handleFilterComplete = (data) => {

  }

  const handleDateClick = (date) => {
    setSelectedDate(date);
  }

  // 런닝 클릭시 상세페이지로 이동하는 로직
  // const handleRunningClick = (id) => {
  //   history.push(`/running/${id}`); 
  // };


  // 캐러셀 동작을 구성하기 위한 설정 객체
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 7, // 동시에 보여질 슬라이드 개수
    slidesToScroll: 7, // 한 번에 스크롤할 슬라이드 개수
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 5,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          initialSlide: 2,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        },
      },
    ],
  };



  useEffect(() => {
    console.log(selectedDate);
    const fetchRunnings = async () => {
      try {
        const response = await axiosInstance.get('/api/runnings/get', {
          params: {
            date: selectedDate, // 선택된 날짜로 API 호출
          },
        });
        setRunnings(response.data.data);
      } catch (error) {
        console.error('Error fetching boards:', error);
        setError('게시판 목록을 가져오는 중 오류가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

    fetchRunnings();
  }, [selectedDate]); // selectedDate가 변경될 때마다 호출




  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
          <div>
      <Slider {...settings}> {/* 설정된 캐러셀 옵션을 전달 */}
        {days}
      </Slider>
    </div>
        <div className='main__filter'>
            <div className='filter--wrapper'>
                <ul>
                    <li className='clickable-text' onClick={() => openModal('startLocation')}>
                        <span>시작지역</span>
                    </li>
                    <li className='clickable-text' onClick={() => openModal()}>
                        <span>도착지역</span>
                    </li>
                    <li className='clickable-text'>
                        <span>마감 가리기</span>
                    </li>
                    <li className='clickable-text'>
                        <span>성별</span>
                    </li>
                    <li className='clickable-text'>
                        <span>거리별</span>
                    </li>
                </ul>
            </div>
        </div>


        <div>
          {runnings.length === 0 ? (
            <div className='no-data-message'><p>런닝이 없습니다.</p></div>
          ):(
        <ul className="running-list">
      {runnings.map((running) => (
        <li key={running.id} className="running-item ">
          <div className="running-item__time">
            <p>{running.time}</p>
          </div>
          <div className="running-item__info">
            <h3>{running.title}</h3>
            <p>{running.content}</p>
            <div className="item-details">
              <p>제한 인원: {running.limitedPeople}명</p>
              <p>시작 위치: {running.startLocation}</p>
              <p>도착 위치: {running.finishLocation}</p>
              <p>거리: {running.distance}km</p>
            </div>
          </div>
        </li>
      ))}
    </ul>
)}
    <Modal
      isOpen={modalIsOpen}
      onRequestClose={closeModal}>
        <h2>지역 선택</h2>

        <button onClick={closeModal}>닫기</button>
      </Modal>
        </div>
    </div>
  );
};

export default RunningList;
