// src/pages/RunningDetailPage.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axiosInstance from '../utils/axiosInstance';
import '../css/Crew/CrewDetailPage.css'; // CSS 파일 import
import Navbar from '../components/NavBar';


const CrewDetailPage = () => {
  const { id } = useParams();
  const [crew, setCrew] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [user, setUser] = useState('');
  const navigate = useNavigate();

  // const [currentUserEmail, setCurrentUserEmail] = useState(null); // 현재 로그인한 사용자 이메일 상태 추가




  // 런닝 게시판 데이터 가져오기
  useEffect(() => {
    const fetchRunningDetail = async () => {
      try {
        const response = await axiosInstance.get(`/api/crews/get/${id}`);
        setCrew(response.data.data); // API에서 반환하는 데이터 구조에 따라 설정
        setUser(response.data.data.user);
        console.log(response.data.data);


      } catch (error) {
        console.error('Error fetching running detail:', error);
        setError('런닝 상세 정보를 불러오는 중 오류가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

    fetchRunningDetail();
  }, [id]);





    // 수정 버튼 클릭 시 처리
    const handleEdit = () => {
        navigate(`/crew/update/${id}`)
      };

      // 삭제 버튼 클릭 시 처리
      const handleDelete = async () => {
        try {
          await axiosInstance.delete(`/api/crews/delete/${id}`);
          alert('런닝이 성공적으로 삭제되었습니다.');
          navigate('/crew');
        } catch (error) {
          alert(error.response.data.errorMessage);
        }
      };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  if (!crew) {
    return <p>Crew not found.</p>;
  }

  const handlePageChange = (path) => {
    navigate(path);
}


  // 좋아요 버튼 클릭 시 처리
  const handleLike = async () => {
    try {
      const response = await axiosInstance.post(`/api/crews/like/${id}`);
      setLiked(response.data.liked); // 서버 응답에 따라 좋아요 상태 업데이트
    } catch (error) {
      console.error('Error liking crew:', error);
      alert('좋아요 처리 중 오류가 발생했습니다.');
    }
  };


  return (
    <div className='container'>
      <div>
        <Navbar />
      </div>
    <div className="crew-detail-container">
      <div className="crew-detail">
      {crew.bannerImg && (
            <img src={crew.bannerImg} alt="크루 배너" className="crew-banner" />
          )}
        <h2>제목: {crew.title}</h2>
        <p>런닝장: {user.nickname}</p>
        <p>내용: {crew.content}</p>
        <p>지역: {crew.location}</p>
        <div>
            <button onClick={handleEdit}>수정</button>
            <button onClick={handleDelete}>삭제</button>
            <button onClick={handleLike}>
              {liked ? '❤️' : '🤍'} 좋아요
            </button>
          </div>

      </div>

    </div>
    </div>
  );
};

export default CrewDetailPage;
