import React, { useEffect, useState } from 'react';
import axiosInstance from '../utils/axiosInstance';

const BoardList = () => {
  const [boards, setBoards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBoards = async () => {
      try {
        const response = await axiosInstance.get('/api/boards/main', {
          params: {
            page: 0, // 페이지 번호
            pageSize: 10, // 페이지 크기
          },
          headers: {
            // 필요한 경우 인증 헤더 추가
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        setBoards(response.data.data); // ApiSuccessResponse의 data 속성 사용
      } catch (error) {
        console.error('Error fetching boards:', error);
        setError('게시판 목록을 가져오는 중 오류가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

    fetchBoards();
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      <h1>Board List</h1>
      <ul>
        {boards.map((board) => (
          <li key={board.id}>
            <h2>{board.title}</h2>
            <p>{board.content}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BoardList;
