import React, { useContext, useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axiosInstance from '../utils/axiosInstance';
import '../css/Crew/CrewDetailPage.css'; // CSS 파일 import
import Header from '../components/Header';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import ReplyForm from '../components/ReplyForm'; // Import the reply form component
import Modal from 'react-modal';
import NavBar from '../components/NavBar';
import { AuthContext } from '../contexts/AuthContext';

const CrewDetailPage = () => {
  const { id } = useParams();
  const [crew, setCrew] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [user, setUser] = useState('');
  const navigate = useNavigate();
  const [liked, setLiked] = useState(false); // 좋아요 상태 관리
  const [modalIsOpen, setModalIsOpen] = useState(false);  // 모달 변수 설정
  const [replyId, setReplyId] = useState(null); // 답글을 달 게시물의 ID
  const { user: currentUser } = useContext(AuthContext); // AuthContext 사용

  // 런닝 게시판 데이터 가져오기
  useEffect(() => {
    const fetchRunningDetail = async () => {
      try {
        const response = await axiosInstance.get(`/api/crews/get/${id}`);
        setCrew(response.data.data); // API에서 반환하는 데이터 구조에 따라 설정
        setUser(response.data.data.user);
        console.log(response.data.data);
      } catch (error) {
        console.error('Error fetching running detail:', error);
        setError('런닝 상세 정보를 불러오는 중 오류가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

    fetchRunningDetail();
  }, [id]);

  // 수정 버튼 클릭 시 처리
  const handleEdit = () => {
    navigate(`/crew/update/${id}`)
  };

  // 삭제 버튼 클릭 시 처리
  const handleDelete = async () => {
    try {
      await axiosInstance.delete(`/api/crews/delete/${id}`);
      alert('런닝이 성공적으로 삭제되었습니다.');
      navigate('/crew');
    } catch (error) {
      alert(error.response.data.errorMessage);
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  if (!crew) {
    return <p>Crew not found.</p>;
  }

  // 좋아요 버튼 클릭 시 처리
  const handleLike = async () => {
    try {
      if (liked) {
        await axiosInstance.post(`/api/likes/delete/${id}`);
      } else {
        await axiosInstance.post(`/api/likes/create/${id}`);
      }
      setLiked(!liked); // 좋아요 상태 토글
    } catch (error) {
      console.error('Error toggling like:', error);
      alert('좋아요 처리 중 오류가 발생했습니다.');
    }
  };

  // 모달에서 답글 달기 버튼 클릭 시 처리
  const handleReplyModalOpen = (replyId) => {
    setReplyId(replyId); // 답글을 달 게시물의 ID 설정
    setModalIsOpen(true); // 모달 열기
  };

  // 모달에서 답글 달기 폼 제출 시 처리
  const handleReplySubmit = async (replyContent) => {
    try {
      // API 호출하여 답글 등록
      await axiosInstance.post(`/api/replies/create/${replyId}`, { content: replyContent });
      alert('답글이 성공적으로 등록되었습니다.');
      // 답글 등록 후 모달 닫기
      closeModal();
      // 다시 런닝 상세 정보 가져오기 (새로운 답글 반영)
    } catch (error) {
      console.error('Error posting reply:', error);
      alert('답글 등록 중 오류가 발생했습니다.');
    }
  };

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setReplyId(null); // 모달이 닫힐 때 replyId 초기화
  };

  return (
    <div className='container'>
      <Header />
      <NavBar />
      <div className="crew-detail-container">
        <div className="crew-detail">
          {crew.bannerImg && (
            <img src={crew.bannerImg} alt="크루 배너" className="crew-banner" />
          )}
          <div className='likes-container'>
            <FontAwesomeIcon
              icon={faHeart}
              className={`clickable heart-icon ${liked ? 'liked' : ''}`}
              onClick={handleLike}
            />
            <p>{crew.likeCount}</p>
          </div>
          <h2>{crew.title}</h2>
          <p>런닝장: {user.nickname}</p>
          <p>소개: {crew.content}</p>
          <p>지역: {crew.location}</p>

          {currentUser && currentUser.id === user.id &&(
            <div>
                <button onClick={handleEdit}>수정</button>
                <button onClick={handleDelete}>삭제</button>
            </div>
          )}

          <ReplyForm crewId={id} />
          <div className="replies">
            {crew.replies && crew.replies.map((reply, index) => (
              <div key={index} className="reply">
                {reply && (
                  <>
                    <div className="reply-user-info">
                      <img
                        src={reply.user.profileImg}
                        alt="프로필 이미지"
                        className="profile-image"
                      />
                      <p>{reply.user.nickname}</p>
                    </div>
                    <h3>{reply.content}</h3>
                    <div className='reply-actions'>
                      <a className='clickable'>삭제</a>
                      <a className='clickable'>수정</a>
                      <a className='clickable'>답글보기</a>
                      {/* 답글달기 버튼에 onClick 이벤트 추가 */}
                      <a className='clickable' onClick={() => handleReplyModalOpen(reply.id)}>답글달기</a>
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
      {/* 답글 달기 모달 */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="modal"
        overlayClassName="modal-overlay"
      >
        <h2>답글 달기</h2>
        {/* ReplyForm 컴포넌트로 답글 입력 기능 구현 */}
        <ReplyForm onSubmit={handleReplySubmit} />
        <button onClick={closeModal}>닫기</button>
      </Modal>
    </div>
  );
};

export default CrewDetailPage;
