import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../utils/axiosInstance';
import KakaoMap from '../utils/KakaoMap';
import DaumPostcode from "react-daum-postcode";
import DaumAddress from '../utils/daumAddress';
import ModalExample from '../utils/ModalExample';
import Modal from 'react-modal';
import '../css/RunningCreatePage.css'; // 스타일 파일을 임포트


function App() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [message, setMessage] = useState('');
  const [distance, setDistance] = useState('');
  const [startLocation, setStartLocation] = useState('');
  const [finishLocation, setFinishLocation] = useState('');
  const [date, setDate] = useState('');
  const navigate = useNavigate();


  const completeHandler = (data) => {
    console.log(data);
  }

  const handlePageChange = (path) => {
    navigate(path);
}

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axiosInstance.post('/api/running/create', {
        title,
        content,
        distance,
        startLocation,
        finishLocation,
        date

      });

      if(response.status === 200) {
        navigate('/');
        setMessage(response.data);
        alert(response.data);
      }
    } catch (error) {
      setMessage(error.response.data.errorMessage);
      alert(error.response.data.errorMessage);
    }
  };

  return (
    <div className='card'>
      <div className='card-header'>
        <h2>런닝 만들기</h2>
      </div>

      <form onSubmit={handleSubmit}>

        <div className='card-write'>
          <div className='title-w'>
            <label>제목 :</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="제목을 입력하세요."
            />
          </div>

          <div className='content-w'>
            <label>내용 :</label>
            <textarea
              placeholder='경로설명, 소개 등등 내용을 입력해주세요.'
              className='content-input'
              type="text"
              value={content}
              onChange={(e) => setContent(e.target.value)}
            />
          </div>

          <div>
            <label>거리:</label>
            <input
              placeholder='소숫점 숫자를 입력해주세요'
              type="number"
              step="0.1"
              value={distance}
              onChange={(e) => setDistance(e.target.value)}
            />
          </div>

          <div>
            <label>시작지점:</label>
            <input
              type="text"
              value={startLocation}
              onChange={(e) => setStartLocation(e.target.value)}
            />
          </div>

          <div>
            <label>도착지점:</label>
            <input
              type="text"
              value={finishLocation}
              onChange={(e) => setFinishLocation(e.target.value)}
            />
          </div>

          <div>
            <label>date:</label>
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
          </div>

        </div>

        <button type="submit">완료</button>

      </form>
      <p>{message}</p>
    </div>
  );
}

export default App;
