import React, { useEffect, useState } from 'react';
import axiosInstance from '../utils/axiosInstance';

const BoardList = () => {
  const [boards, setBoards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(0); // 현재 페이지
  const pageSize = 10; // 페이지 당 보여질 아이템 수
  const [totalPages, setTotalPages] = useState(0); // 총 페이지 수

  useEffect(() => {
    const fetchBoards = async () => {
      try {
        const response = await axiosInstance.get('/api/boards/main', {
          params: {
            page: currentPage,
            pageSize: pageSize,
          },
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        setBoards(response.data.data);
        setTotalPages(Math.ceil(response.data.total / pageSize)); // 전체 페이지 수 계산
      } catch (error) {
        console.error('Error fetching boards:', error);
        setError('게시판 목록을 가져오는 중 오류가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

    fetchBoards();
  }, [currentPage]); // currentPage가 변경될 때마다 호출

  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      setCurrentPage(newPage);
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  // 페이지 네비게이션 버튼을 렌더링하는 함수
  const renderPaginationButtons = () => {
    const pages = [];
    const range = 2; // 현재 페이지를 중심으로 좌우로 표시될 페이지 버튼의 수

    // 현재 페이지 기준 좌우로 range 만큼 페이지 번호 버튼 추가
    for (let i = Math.max(0, currentPage - range); i <= Math.min(currentPage + range, totalPages - 1); i++) {
      pages.push(
        <button key={i} onClick={() => handlePageChange(i)} disabled={currentPage === i}>
          {i + 1}
        </button>
      );
    }

    return pages;
  };

  return (
    <div>
      <h1>Board List</h1>
      <ul>
        {boards.map((board) => (
          <li key={board.id}>
            <h2>{board.title}</h2>
            <p>{board.content}</p>
          </li>
        ))}
      </ul>
      <div>
        {/* 페이지 네비게이션 UI */}
        <button onClick={() => handlePageChange(0)} disabled={currentPage === 0}>
          이전 끝
        </button>
        <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 0}>
          이전 페이지
        </button>
        {renderPaginationButtons()}
        <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages - 1}>
          다음 페이지
        </button>
        <button onClick={() => handlePageChange(totalPages - 1)} disabled={currentPage === totalPages - 1}>
          다음 끝
        </button>
      </div>
    </div>
  );
};

export default BoardList;
