import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/Crew/Crew.css'; // 스타일 파일을 임포트
import Navbar from '../components/NavBar';
import axiosInstance from '../utils/axiosInstance';




const CrewPage = () => {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [crews, setCrews] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchCrew = async () => {
            try {
                const response = await axiosInstance.get('/api/crews/get', {
                  params: {
                  },
                });
                setCrews(response.data.data || []); // 데이터를 설정하고, 없으면 빈 배열로 초기화
                console.log(response.data.data);
              } catch (error) {
                console.error('Error fetching boards:', error);
                setError('게시판 목록을 가져오는 중 오류가 발생했습니다.');
              } finally {
                setLoading(false);
              }
        }

        fetchCrew();
    });



    return (
        <div className='container'>
            <div>
                <Navbar />
            </div>

            {runnings.length === 0 ? (
          <div className='no-data-message'><p>런닝이 없습니다.</p></div>
        ):(
          <ul className="running-list">
            {runnings.map((running) => (
              <li key={running.id} className="running-item clickable" onClick={() => navigateToDetail(running.id)}>
                <div className="running-item__time">
                  <p>{running.time}</p>
                </div>
                <div className="running-item__info">
                  <h3>{running.title}</h3>
                  <p>{running.content}</p>
                  <div className="item-details">
                    <p>제한 인원: {running.limitedPeople}명</p>
                    <p>시작 위치: {running.startLocation}</p>
                    <p>도착 위치: {running.finishLocation}</p>
                    <p>인원: {running.runningUserCount} / {running.limitedPeople}{running.runningStatus === 'COMPLETE' ? '마감완료' : '신청가능'}</p>
                    <p></p>
                    <p>거리: {running.distance}km</p>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        )}
        </div>
    );
};

export default CrewPage;
